import time
import string

def palindrome_identifier():
    input_string = input('Please enter a string: ')

    duration = -time.time()

    # Naive approach
    # Compare first and last chars and continue comparison until reaching string mid-point
    # Since this is scanning the entire string once, this may be the most efficient approach
    length = len(input_string)

    left = 0
    right = length - 1

    while left <= right:
        if input_string[left] != input_string[right]:
            duration += time.time()
            print(f'Execution time: {duration}')

            return False

        left += 1
        right -= 1

    duration += time.time()
    print(f'Execution time: {duration}')

    return True

print(palindrome_identifier())

